site.headStyle:
html {
    scroll-behavior: smooth;
    } 

.middleplan {
    z-index: 500 !important;
}

.alcove {
        border-radius: 150px 150px 10px 10px;
}

/*
menu
*/
.rotate90 {
transform: rotate(-90deg);
}
.fullheight{
    height: 100%;
}
.fixed{
    position:fixed;
}
.firstplan{
    z-index: 1000 !important;
}


/*
retrait du padding de 10px sur mobile
*/
.no-padding {
    padding: 0px !important;
}


/*
form
*/
.form input, textarea {
   color: #616f8b;
}
label {
    font-family: 'Lato';
    text-transform: uppercase;
    font-weight: bolder;
}
.fill-vertical textarea {
    border: 0px !important;
    border-radius: none !important;
    background-color: #ffff !important;
    margin-top: 10px;
    margin-bottom: 10px;
}
.form div, input {
    border: none !important;
    border-radius: none !important;
}
.form button, input, select, textarea {
    font-family: 'Lato';
    /*text-transform: uppercase;*/
    font-weight: bolder;
    color: #616f8b;
}

.form input, textarea {
    text-transform: none;
}

button, input, select, textarea {
    text-transform: none !important;
    color: #616f8b;
}


